import React from "react";

const Login = () => {
  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
    <div className="relative bg-white p-8 rounded-lg shadow-xl max-w-md w-full transition-transform transform hover:scale-105 duration-500 ease-in-out">
      
      {/* Form Title */}
      <h2 className="text-3xl font-bold text-center text-gray-700 mb-6 animate-pulse">Login</h2>

      <form className="space-y-6">
        {/* Email Field */}
        <div className="relative">
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full px-4 py-3 text-gray-900 placeholder-gray-500 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 ease-in-out"
            placeholder="Email"
          />
          <span className={`absolute left-4 top-1/2 transform -translate-y-1/2 text-blue-500 transition-all duration-300 ease-in-out ${email ? 'opacity-100' : 'opacity-0'}`}>
            <svg className="w-6 h-6" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" d="M16 12V7a4 4 0 10-8 0v5m8 0a4 4 0 01-8 0m8 0H5a2 2 0 012-2h10a2 2 0 012 2z" />
            </svg>
          </span>
        </div>

        {/* Password Field */}
        <div className="relative">
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full px-4 py-3 text-gray-900 placeholder-gray-500 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 transition-all duration-300 ease-in-out"
            placeholder="Password"
          />
          <span className={`absolute left-4 top-1/2 transform -translate-y-1/2 text-blue-500 transition-all duration-300 ease-in-out ${password ? 'opacity-100' : 'opacity-0'}`}>
            <svg className="w-6 h-6" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" d="M12 17h.01M9 11V7a4 4 0 118 0v4a2 2 0 01-2 2h-4a2 2 0 01-2-2z" />
            </svg>
          </span>
        </div>

        {/* Login Button */}
        <button
          type="submit"
          className="w-full py-3 text-white font-semibold rounded-lg bg-gradient-to-r from-blue-500 to-indigo-500 shadow-lg transform hover:scale-105 transition-transform duration-300 ease-in-out"
        >
          Login
        </button>

        {/* Forgot Password */}
        <div className="text-center mt-4">
          <a href="#" className="text-sm text-blue-500 hover:underline">
            Forgot Password?
          </a>
        </div>
      </form>

      {/* Divider and Sign Up Link */}
      <div className="mt-6 text-center">
        <p className="text-sm text-gray-600">Don't have an account?{" "}
          <a href="#" className="text-blue-500 hover:underline">Sign up</a>
        </p>
      </div>
    </div>
  </div>
  );
};

export default Login;
